function setupHTML(index) {

  var idstr = index.toString()
  var object = mdm.store.data__.api.NHS_KPI_Dashboard.GetViewData.data.data.kpis[index];

  document.getElementsByName('title'+idstr)[0].placeholder = object.name;
  document.getElementsByName('no'+idstr)[0].placeholder = object.value;
  if(jQuery.isEmptyObject(object.nameinfo)==false) document.getElementsByName('help'+idstr)[0].placeholder = object.nameinfo;

  document.getElementsByName('title'+idstr)[0].value = "";		//clear defaults
  document.getElementsByName('no'+idstr)[0].value = "";			//clear defaults
  document.getElementsByName('help'+idstr)[0].value = "";		//clear defaults

  document.getElementById('circle'+idstr).className = "level"+object.level;

  var change = object.change;
  var domitem = document.getElementById("arrow"+idstr);

  if(change == "=") domitem.className = "arrowSame";
  else if(change == "+") domitem.className = "arrowUp";
  else if(change == "-") domitem.className = "arrowDown";

  if(jQuery.isEmptyObject(object.valueinfo)==false) document.getElementById("valueinfo"+idstr).innerHTML = object.valueinfo;
  document.getElementById("iconrow"+idstr).className = "icon"+object.icon;

}

