function extractHTML(index) {

  var idstr = index.toString()
  var kpi_object = mdm.store.data__.api.NHS_KPI_Dashboard.GetViewData.data.data.kpis[index];

  var newtext = document.getElementsByName('title'+idstr)[0].value;
  if(newtext != "") kpi_object.name = newtext;

  var newnumber = document.getElementsByName('no'+idstr)[0].value;
  if(newnumber != "") kpi_object.value = parseInt(newnumber,10);

  var newhelp = document.getElementsByName('help'+idstr)[0].value;
  if(newhelp != "") kpi_object.nameinfo = newhelp;

  var domitem = document.getElementById("arrow"+idstr);

  if(domitem.className == "arrow") kpi_object.change;
  else if(domitem.className == "arrowUp") domitem.className;
  else if(domitem.className == "arrowDown") domitem.className;

  kpi_object.valueinfo = document.getElementById("valueinfo"+idstr).placeholder;

  var iconnumber = document.getElementById("iconrow"+idstr).className.slice(-1);
  kpi_object.icon = parseInt(iconnumber,10);

}
